let dictionaryData=null;const dataURL="./data.json",getData=fetch(dataURL).then(response=>response.json()).then(data=>{dictionaryData=data}).catch(error=>{console.log(error)});function alphabetSort(words){return words.sort((s1,s2)=>s1.localeCompare(s2))}function findWordOrder(word){return word.toLowerCase()[0].charCodeAt(0)-97}function searchWord(word){const displayWord=document.querySelector(".display-word");let wordOrder=findWordOrder(word),html="";const listWords=alphabetSort(Object.keys(dictionaryData[wordOrder]));listWords.map(key=>{if(key===word)return html=`<h2>${key}</h2>\n      <p>${dictionaryData[wordOrder][key]}</p>`,void(displayWord.innerHTML=html)})}function searchHintsWord(searchWord){const searchedWords=[];let searchWordOrder=findWordOrder(searchWord);const sortedWords=alphabetSort(Object.keys(dictionaryData[searchWordOrder]));for(const word of sortedWords){if(5==searchedWords.length)return searchedWords;word.startsWith(searchWord)&&searchedWords.push(word)}return searchedWords}function displayHintsWord(searchWords){const listHintWord=document.querySelector(".searched");let html="";searchWords.map(word=>{html+=`<p onclick="searchWord(this.innerHTML)">${word}</p>`}),listHintWord.innerHTML=html}const inputTag=document.querySelector("#input");inputTag.oninput=event=>{if(event.target.value){const listWord=searchHintsWord(event.target.value);displayHintsWord(listWord)}};